<script setup>
const props = defineProps({
  imgUrl: {
    type: String,
    default:"data:image/png;base64,"
 }
})

const fileInput = ref(null)
const previewUrl = ref(props.imgUrl)
const selectedFile = ref(null)

const triggerFileInput = () => {
  fileInput.value.click()
}

const handleFileChange = (event) => {
  const file = event.target.files[0]
  if (!file) return

  selectedFile.value = file
  const reader = new FileReader()
  reader.onload = (e) => {
    previewUrl.value = e.target.result
  }
  reader.readAsDataURL(file)
  event.target.value = null
}

// 暴露文件对象给父组件
defineExpose({
  getFile: () => selectedFile.value,
  getUrl: () => previewUrl.value
})
</script>

<template>
  <div class="upload-wrapper">
    <input ref="fileInput" type="file" accept="image/png, image/jpeg" class="hidden-input" @change="handleFileChange" />
    <div class="upload-area">
      <img src="@/assets/upload.png" @click="triggerFileInput" class="default-image" alt="Upload" />
      <img :src="previewUrl" class="preview-image" alt="Preview" />
    </div>
  </div>
</template>

<style scoped>
.upload-wrapper {
  position: relative;
  display: inline-block;
}

.hidden-input {
  display: none;
}

.upload-area {
  display: flex;
  align-items: flex-end;
  justify-content: center;
  overflow: hidden;
  gap: 5px;
}
.default-image {
  cursor: pointer;
  border: 2px dashed #ccc;
  border-radius: 8px;
  width: 40px;
  height: 40px;
  opacity: 0.6;
}
.default-image:hover {
  border-color: #409eff;
  opacity: 1;
}

.preview-image {
  border-radius: 8px;
  border: 2px solid #ccc;
  width: 60px;
  height: 60px;
  object-fit: cover;
}
</style>